<template>
	<!-- 自定义导航 -->
	<view class="goback" :style="'top:'+navbarTop+'px'" @click="goback">
		<image class="back_img"
			src="data:image/png;base64,">
		</image>
	</view>
</template>

<script>
	export default {
		data() {
			return {
				navbarTop: 30
			}
		},

		created() {
			let res = uni.getSystemInfoSync();
			this.navbarTop = res.statusBarHeight + 5
		},
		methods:{
			goback(){
				uni.navigateBack({
					delta:1
				})
			}
		}

	}
</script>

<style>
	/* // 返回按钮 */
	.goback {
		position: fixed;
		top: 50px;
		left: 20px;
		width: 30px;
		height: 30px;
		line-height: 60px;
		color: #fff;
	}

	.back_img {
		width: 20px;
		height: 20px;
	}
</style>
